import { useState } from 'react';
import { LogIn, ShoppingCart, Tag, User, Heart, Home, Package } from 'lucide-react';
import { motion } from 'framer-motion';
import { useNavigate, useLocation } from 'react-router-dom';

function Navbar() {
  const navigate = useNavigate();
  const location = useLocation();

  const cartItemCount = 5; // Example static count, replace with dynamic data

  const isActiveLink = (path) => location.pathname === path;

  return (
    <motion.nav
      className="fixed top-0 left-0 w-full bg-[#333333] text-gray-100 shadow-lg p-4 flex items-center justify-between z-50" // Dark gray background
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      {/* Logo and Brand Name */}
      <div
        className="flex items-center space-x-3 cursor-pointer"
        onClick={() => navigate('/')}
      >
        <img src="data:image/jpeg;base64," alt="Logo" className="h-12" />
        <span className="font-semibold text-2xl text-[#4A90E2]">Madcappery Store</span>  
      </div>

      {/* Navigation Links and Icons */}
      <div className="flex items-center space-x-8">
        {/* Home Icon */}
        <div
          onClick={() => navigate('/')}
          className={`flex flex-col items-center justify-center cursor-pointer p-2 transition-all duration-200 rounded-lg ${
            isActiveLink('/') ? 'bg-[#4A90E2] text-gray-900' : 'hover:bg-gray-700'
          }`}  // Primary Blue for active state, gray hover
        >
          <Home size={24} />
          <span className="text-sm mt-1">Home</span>
        </div>

        {/* Products Icon */}
        <div
          onClick={() => navigate('/products')}
          className={`flex flex-col items-center justify-center cursor-pointer p-2 transition-all duration-200 rounded-lg ${
            isActiveLink('/products') ? 'bg-[#4A90E2] text-gray-900' : 'hover:bg-gray-700'
          }`}  // Primary Blue for active state
        >
          <Package size={24} />
          <span className="text-sm mt-1">Products</span>
        </div>

        {/* Cart Icon */}
        <div
          onClick={() => navigate('/cart')}
          className={`flex flex-col items-center justify-center cursor-pointer p-2 transition-all duration-200 rounded-lg ${
            isActiveLink('/cart') ? 'bg-[#4A90E2] text-gray-900' : 'hover:bg-gray-700'
          }`}  // Primary Blue for active state
        >
          <ShoppingCart size={24} />
          <span className="text-sm mt-1">Cart</span>
        </div>

        {/* Offers Icon */}
        <div
          onClick={() => navigate('/offers')}
          className={`flex flex-col items-center justify-center cursor-pointer p-2 transition-all duration-200 rounded-lg ${
            isActiveLink('/offers') ? 'bg-[#4A90E2] text-gray-900' : 'hover:bg-gray-700'
          }`}  // Primary Blue for active state
        >
          <Tag size={24} />
          <span className="text-sm mt-1">Offers</span>
        </div>

        {/* Favourite Icon */}
        <div
          onClick={() => navigate('/favourite')}
          className={`flex flex-col items-center justify-center cursor-pointer p-2 transition-all duration-200 rounded-lg ${
            isActiveLink('/favourite') ? 'bg-[#4A90E2] text-gray-900' : 'hover:bg-gray-700'
          }`}  // Primary Blue for active state
        >
          <Heart size={24} />
          <span className="text-sm mt-1">Favourites</span>
        </div>

        {/* About Us Section */}
<div 
  onClick={() => navigate('/about')}
  className={`flex flex-col items-center justify-center cursor-pointer p-2 transition-all duration-200 rounded-lg ${
    isActiveLink('/about') ? 'bg-[#4A90E2] text-gray-900' : 'hover:bg-gray-700'
  }`}
>
  <svg 
    xmlns="http://www.w3.org/2000/svg" 
    width="24" 
    height="24" 
    viewBox="0 0 24 24" 
    fill="none" 
    stroke="currentColor" 
    strokeWidth="2" 
    strokeLinecap="round" 
    strokeLinejoin="round"
    className="feather feather-info"
  >
    <circle cx="12" cy="12" r="10"></circle>
    <line x1="12" y1="16" x2="12" y2="12"></line>
    <line x1="12" y1="8" x2="12.01" y2="8"></line>
  </svg>
  <span className="text-sm mt-1">About Us</span>
</div>

        {/* Profile Icon */}
        <div
          onClick={() => navigate('/profile')}
          className={`flex flex-col items-center justify-center cursor-pointer p-2 transition-all duration-200 rounded-lg ${
            isActiveLink('/profile') ? 'bg-[#4A90E2] text-gray-900' : 'hover:bg-gray-700'
          }`}  // Primary Blue for active state
        >
          <User size={24} />
          <span className="text-sm mt-1">Profile</span>
        </div>
        
      </div>
    </motion.nav>
  );
}

export default Navbar;
